clear dseis;% plotting the velocity model and the resulting synthetic seismogramsubplot(1,2,1);imagesc(xl,zl,velmod2); hold on;plot(xr,zr,'k<'); q1 = colorbar; q2 = get(q1,'YLabel');set(q2,'String','V_p (m/s)');%axis equal;axis tight;xlabel('X (m)'); ylabel('Depth (m)'); title('Velocity Model');pause;di = 1;for is =1:di:sCnt;xshot = xs(is);zshot = zs(is);disp(cat(1,['Shot Depth = ',num2str(zshot)]));subplot(1,2,1);plot(xshot,zshot,'ko'); dseis(:,:) = seis(is,:,:);subplot(1,2,2);imagesc(tarr,zr,dseis); hold on;%plot(0,zshot,'ro');axis([min(tarr),max(tarr),min(zl),max(zl)]);caxis([-0.025,0.025]);q1 = colorbar; q2 = get(q1,'YLabel');set(q2,'String','Amplitude');set(gca,'xgrid','on')ylabel('Receiver Z (m)'); xlabel('Time (mS)');title(cat(1,['Synthetic: CS = ',num2str(zshot)]));orient landscape;pause;    set(gca,'units','points');end